import React, {Component} from 'react'
import AuthContext from '../context/AuthContext'
import {cakeApi} from '../misc/CakeApi'
import {handleLogError} from '../misc/Helpers'
import {Container} from "semantic-ui-react";
import CakeTable from "./CakeTable";

class Download extends Component {
    static contextType = AuthContext

    state = {
        userMe: null,
        isUser: true,
        isLoading: false,
        cakeDescription: '',
        image: '',
        title: '',
        data: []
    }

    componentDidMount() {

        this.handleDownloadCakes()

    }


    handleDownloadCakes = () => {
        const Auth = this.context
        const user = Auth.getUser()

        this.setState({isLoading: true})
        cakeApi.download(user)
            .then(response => {
                response.blob().then(blob => {
                    let url = window.URL.createObjectURL(blob);
                    let a = document.createElement('a');
                    a.href = url;
                    a.download = 'employees.json';
                    a.click();
                });
                //window.location.href = response.url;
            })
            .catch(error => {
                handleLogError(error)
            })
            .finally(() => {
                this.setState({isLoading: false})
            })
    }

    render() {
        return (
            <div id="container">
                <h1>Download File using React App</h1>
                <h3>Download Employee Data using Button</h3>
                <button onClick={this.handleDownloadCakes}>Download</button>
                <p/>
                <h3>Download Employee Data using Link</h3>
                <a href="#" onClick={this.handleDownloadCakes}>Download</a>
            </div>
        )
    }

}

export default Download
