import React, {Component} from 'react'
import {Redirect} from 'react-router-dom'
import {Container} from 'semantic-ui-react'
import OrderTable from './OrderTable'
import AuthContext from '../context/AuthContext'
import {cakeApi} from '../misc/CakeApi'
import {handleLogError} from '../misc/Helpers'

class AddCake extends Component {
    static contextType = AuthContext

    state = {
        userMe: null,
        isUser: true,
        isLoading: false,
        cakeDescription: '',
        image: '',
        title: ''
    }

    componentDidMount() {
        const Auth = this.context
        const user = Auth.getUser()
        const isUser = user.data.rol[0] === 'USER'
        this.setState({isUser})

    }

    handleInputChange = (e, {name, value}) => {
        this.setState({[name]: value})
    }

    handleGetUserMe = () => {
        const Auth = this.context
        const user = Auth.getUser()

        this.setState({isLoading: true})
        cakeApi.getUserMe(user)
            .then(response => {
                this.setState({userMe: response.data})
            })
            .catch(error => {
                handleLogError(error)
            })
            .finally(() => {
                this.setState({isLoading: false})
            })
    }
    handleCreateCake = () => {
        const Auth = this.context
        const user = Auth.getUser()

        let {cakeDescription} = this.state
        let {image} = this.state
        let {title} = this.state

        cakeDescription = cakeDescription.trim()
        image = image.trim()
        title = title.trim()

        const cake = {description: cakeDescription, image: image, title: title}
        cakeApi.createCake(user, cake)
            .then(() => {
                this.handleGetUserMe()
                this.setState({cakeDescription: '', title: '', image: ''})
            })
            .catch(error => {
                handleLogError(error)
            })
    }

    render() {
        if (!this.state.isUser) {
            return <Redirect to='/'/>
        } else {
            const {userMe, isLoading, cakeDescription} = this.state
            return (
                <Container>
                    <OrderTable
                        orders={userMe && userMe.orders}
                        isLoading={isLoading}
                        cakeDescription={cakeDescription}
                        handleCreateCake={this.handleCreateCake}
                        handleInputChange={this.handleInputChange}
                    />
                </Container>
            )
        }
    }
}

export default AddCake
